    private int getFlag(String url) {
        int flag = URI_INVALID;
        if (url.startsWith("tel:")) {
            flag = URI_PHONE;
        } else if (url.startsWith("http:") || url.startsWith("https:")) {
            flag = URI_HTTP;
        } else if (url.startsWith("mailto:")) {
            flag = URI_MAIL;
        } else if (url.startsWith("rtsp:")) {
            flag = URI_RTSP;
        }
        return flag;
    }
