    private boolean isResume() {
        try {
            Field field = getClass().getSuperclass().getSuperclass().getDeclaredField("mResumed");
            if (field != null) {
                field.setAccessible(true);
                return field.getBoolean(ComposeMessageActivity.this);
            }
        } catch(Exception e) {
            e.printStackTrace();
        }
        return false;
    }
